<template>
<div id="home" class="container mt-5">
    <div class="row justify-content-center">
        <div class="col-md-6">
            <div class="card card-body">
                <div class="form-group">
                    <v-layout row>
                        <v-flex xs12 sm5 offset-sm1>
                            <v-text-field v-model="form.start_date" :type="'date'" color="blue darken-2" label="Start Date" required></v-text-field>
                        </v-flex>
                        <v-flex xs12 sm5 offset-sm1>
                            <v-text-field v-model="form.end_date" :type="'date'" color="blue darken-2" label="End Date" required></v-text-field>
                        </v-flex>
                    </v-layout>
                </div>
                <v-btn raised color="primary" @click="getScheduled" :disabled="loading" :loading="loading">Download PDF</v-btn>
            </div>
        </div>
    </div>
</div>
</template>

<script>
// import jsPDF from "jsPDF";
import VueBarcode from "vue-barcode";

export default {
    name: "print",
    components: {
        barcode: VueBarcode
    },
    data() {
        return {
            name: "",
            Scheduled: [],
            form: {},
            errors: [],
            loading: false
        };
    },
    methods: {
        getScheduled() {
            this.loading = true;
            axios
                .post("/getScheduled", this.$data.form)
                .then(response => {
                    this.loading = false;
                    this.Scheduled = response.data;
                    this.download();
                })
                .catch(error => {
                    this.loading = false;
                    this.errors = error.response.data.errors;
                });
        },
        download() {
            let pdfName = "Speedball";
            var doc = new jsPDF();
            // doc.text(this.name, 10, 10);
            var doc = new jsPDF();
            var imgData =
                "data:image/png;base64,";

            for (let i = 0; i < this.Scheduled.length; i++) {
                doc.addImage(imgData, 20, 10, 20, 20);
                doc.setFontSize(12);
                const element = this.Scheduled[i];
                doc.setFont("times");
                doc.setFontType("normal");
                doc.text(130, 40, "WayBill Number:" + element["bar_code"]);

                doc.text(100, 70, "WAYBILL");
                // Rectangles
                doc.rect(18, 75, 170, 185);
                doc.rect(18, 75, 170, 10);
                doc.text(100, 80, "DISPATCH SECTION", null, null, "center");
                doc.rect(18, 85, 170, 30);
                doc.text(20, 91, "From: " + element["sender_address"]);
                doc.setFontSize(11);
                doc.text(20, 109, "To: " + element["client_address"]);
                doc.text(120, 91, "Dispatch Date: " + element["derivery_date"]);
                doc.setFontSize(12);
                // Sender Details
                doc.rect(18, 115, 170, 10);
                doc.setFontSize(14);
                doc.text(100, 120, "Sender Details", null, null, "center");
                doc.setFontSize(12);
                doc.rect(18, 125, 170, 20);
                doc.text(20, 132, "Sent By: Speedball Services");
                doc.text(120, 132, "Phone: +25420760877");
                doc.text(20, 139, "Email:speedball.co.ke");
                // Client Details
                doc.rect(18, 145, 170, 10);
                doc.setFontSize(14);
                doc.text(100, 151, "Client Details", null, null, "center");
                doc.setFontSize(12);
                doc.rect(18, 155, 170, 20);
                doc.text(20, 160, "Client: " + element["client_name"]);
                doc.text(120, 160, "Phone: " + element["client_phone"]);
                doc.text(20, 170, "Email: " + element["client_email"]);
                // doc.text(120, 170, 'Address: ' + element['client_address']);

                // Product
                doc.rect(18, 175, 170, 10);
                doc.setFontSize(14);
                doc.text(100, 181, "Product Details", null, null, "center");
                doc.setFontSize(12);
                doc.rect(18, 185, 170, 10);
                doc.text(20, 191, "Product Description");
                doc.text(80, 191, "Price", null, null, "left");
                doc.text(150, 191, "Quantity", null, null, "left");

                doc.rect(18, 195, 170, 15);
                doc.line(70, 195, 70, 198); // vertical line
                doc.line(140, 195, 140, 198); // vertical line
                doc.text(20, 200, "" + element["bar_code"]);
                doc.text(80, 200, "" + element["cod_amount"], null, null, "left");
                doc.text(150, 200, "" + element["amount_ordered"], null, null, "left");

                // Lines

                doc.rect(18, 210, 170, 20);
                doc.setFontSize(14);
                doc.text(
                    20,
                    220,
                    "Special Instructions: " + element["speciial_instruction"]
                );

                doc.rect(18, 230, 170, 30);
                doc.text(20, 235, "NOTE: ");
                doc.text(
                    20,
                    240,
                    "Clients are requested to pay through M-PESA TILL NUMBER - 877838 "
                );
                doc.text(
                    20,
                    248,
                    "(However cash payments will be acceptable in case to case bases) "
                );

                doc.setFontSize(12);
                doc.rect(18, 260, 170, 20);
                doc.text(20, 270, "Authorizer Signature__________________");
                doc.text(
                    185,
                    270,
                    "Customer Signature___________________",
                    null,
                    null,
                    "right"
                );
                doc.addPage();
            }
            doc.save(pdfName + ".pdf");
        }
    },
    mounted() {}
};
</script>
